import sys
import unittest
import libsbml


class TestRDFAnnotationMetaid(unittest.TestCase):

  global d
  d = None
  global m
  m = None

  def setUp(self):
    filename = "../../sbml/annotation/test/test-data/annotationL3_3.xml"
    self.d = libsbml.readSBML(filename)
    self.m = self.d.getModel()
    filename = None
    pass  

  def tearDown(self):
    self.d = None
    pass  

  def test_RDFAnnotationMetaid_setAnnotation1(self):
    rdfAnn = wrapString("<annotation>\n" + 
    "  <rdf:RDF xmlns:rdf=\"http://www.w3.org/1999/02/22-rdf-syntax-ns#\" xmlns:dcterms=\"http://purl.org/dc/terms/\" xmlns:vCard=\"http://www.w3.org/2001/vcard-rdf/3.0#\" xmlns:vCard4=\"http://www.w3.org/2006/vcard/ns#\" xmlns:bqbiol=\"http://biomodels.net/biology-qualifiers/\" xmlns:bqmodel=\"http://biomodels.net/model-qualifiers/\">\n" + 
    "    <rdf:Description rdf:about=\"#_002\">\n" + 
    "      <dcterms:creator>\n" + 
    "        <rdf:Bag>\n" + 
    "          <rdf:li rdf:parseType=\"Resource\">\n" + 
    "            <vCard:N rdf:parseType=\"Resource\">\n" + 
    "              <vCard:Family>Le Novere</vCard:Family>\n" + 
    "              <vCard:Given>Nicolas</vCard:Given>\n" + 
    "            </vCard:N>\n" + 
    "            <vCard:EMAIL>lenov@ebi.ac.uk</vCard:EMAIL>\n" + 
    "            <vCard:ORG rdf:parseType=\"Resource\">\n" + 
    "              <vCard:Orgname>EMBL-EBI</vCard:Orgname>\n" + 
    "            </vCard:ORG>\n" + 
    "          </rdf:li>\n" + 
    "        </rdf:Bag>\n" + 
    "      </dcterms:creator>\n" + 
    "      <dcterms:created rdf:parseType=\"Resource\">\n" + 
    "        <dcterms:W3CDTF>2005-02-02T14:56:11Z</dcterms:W3CDTF>\n" + 
    "      </dcterms:created>\n" + 
    "      <dcterms:modified rdf:parseType=\"Resource\">\n" + 
    "        <dcterms:W3CDTF>2006-05-30T10:46:02Z</dcterms:W3CDTF>\n" + 
    "      </dcterms:modified>\n" + 
    "      <bqbiol:is>\n" + 
    "        <rdf:Bag>\n" + 
    "          <rdf:li rdf:resource=\"http://www.geneontology.org/#GO:0007274\"/>\n" + 
    "        </rdf:Bag>\n" + 
    "      </bqbiol:is>\n" + 
    "    </rdf:Description>\n" + 
    "  </rdf:RDF>\n" + 
    "</annotation>")
    self.assertTrue( self.m.setAnnotation(rdfAnn) == libsbml.LIBSBML_MISSING_METAID )
    self.assertTrue( self.m.getAnnotation() == None )
    self.m.setMetaId("_002")
    self.assertTrue( self.m.setAnnotation(rdfAnn) == libsbml.LIBSBML_OPERATION_SUCCESS )
    self.assertTrue( self.m.getAnnotation() != None )
    self.assertEqual( True, self.equals(rdfAnn,self.m.getAnnotation().toXMLString()) )
    pass  

