import sys
import unittest
import libsbml


class TestReadFromFileL3V2_1(unittest.TestCase):


  def test_echo_l3v2_no_model(self):
    expected = wrapString("<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n" + 
    "<sbml xmlns=\"http://www.sbml.org/sbml/level3/version2/core\" " + 
    "level=\"3\" version=\"2\"/>\n")
    filename = "../../sbml/sbml/test/test-data/"
    filename += "l3v2-no-model.xml"
    fileout = "../../sbml/sbml/test/test-data/"
    fileout += "tempOut.xml"
    d = libsbml.readSBML()
    if (d == None):
      pass    
    libsbml.writeSBML(d)
    outD = libsbml.readSBML()
    self.assertTrue( d.getModel() == None )
    self.assertTrue( outD.getModel() == None )
    S = libsbml.writeSBMLToString(outD)
    self.assertEqual( True, self.equals(expected,S) )
    d = None
    outD = None
    pass  

  def test_read_l3v2_no_model(self):
    reader = libsbml.SBMLReader()
    filename = "../../sbml/sbml/test/test-data/"
    filename += "l3v2-no-model.xml"
    d = reader.readSBML(filename)
    if (d == None):
      pass    
    self.assertTrue( d.getLevel() == 3 )
    self.assertTrue( d.getVersion() == 2 )
    m = d.getModel()
    self.assertTrue( m == None )
    d = None
    pass  

def suite():
  suite = unittest.TestSuite()
  suite.addTest(unittest.makeSuite(TestReadFromFileL3V2_1))

  return suite

if __name__ == "__main__":
  if unittest.TextTestRunner(verbosity=1).run(suite()).wasSuccessful() :
    sys.exit(0)
  else:
    sys.exit(1)
