    "</annotation>"
    s = @@m.getSpecies(0)
    assert( s.setAnnotation(rdfAnn) == LibSBML::LIBSBML_MISSING_METAID )
    assert( s.getAnnotation() == nil )
    s.setMetaId("_003")
    assert( s.setAnnotation(rdfAnn) == LibSBML::LIBSBML_OPERATION_SUCCESS )
    ann = s.getAnnotation()
    assert( ann != nil )
    annot = ann.toXMLString()
    assert_equal true, equals(rdfAnn,)
  end

end
