    assert ((  "eq" == c.getName() ))
    assert( c.getNumChildren() == 2 )
    c = c.getLeftChild()
    assert( c.getType() == LibSBML::AST_INTEGER )
    assert( c.getInteger() == 2 )
    assert( c.getNumChildren() == 0 )
