require 'test/unit'
require 'libSBML'

class TestReadFromFileL3V2 < Test::Unit::TestCase

  def test_read_MathML_L3V2
    expected = "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n" + 
    "<math xmlns=\"http://www.w3.org/1998/Math/MathML\">\n" + 
    "  <apply>\n" + 
    "    <max/>\n" + 
    "    <cn> 2 </cn>\n" + 
    "    <cn> 3 </cn>\n" + 
    "  </apply>\n" + 
    "</math>"
    filename = "../../sbml/math/test/test-data/"
    filename += "L3V2Math.xml"
    d = LibSBML::readSBML()
    if (d == nil)
    end
    m = d.getModel()
    assert( m != nil )
    ia = m.getInitialAssignment(0)
    math = ia.getMath()
    out = writeMathMLToStdString
    assert_equal true, equals(expected,)
    expected1 = "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n" + 
    "<math xmlns=\"http://www.w3.org/1998/Math/MathML\">\n" + 
    "  <apply>\n" + 
    "    <times/>\n" + 
    "    <cn> 2 </cn>\n" + 
    "    <cn> 3 </cn>\n" + 
    "  </apply>\n" + 
    "</math>"
    ia = m.getInitialAssignment(1)
    math = ia.getMath()
    assert( math.getNumPlugins() == 0 )
    out = writeMathMLToStdString
    assert_equal true, equals(expected1,)
    d = nil
  end

end
