require 'test/unit'
require 'libSBML'

class TestLevelCompatibility < Test::Unit::TestCase

  def test_LevelCompatibility_unit_fails
    reader = LibSBML::SBMLReader.new()
    filename = "../../sbml/sbml/test/test-data/"
    filename += "inconsistent-l2v1-units.xml"
    d = reader.readSBML(filename)
    if (d == nil)
    end
    n = d.checkL2v1Compatibility()
    assert( n == 1 )
    assert( d.getError(0).getErrorId() == StrictUnitsRequiredInL2v1 )
    d.getErrorLog().clearLog()
    n = d.checkL2v1Compatibility(true)
    assert( n == 0 )
    d = nil
  end

