require 'test/unit'
require 'libSBML'

class TestReadFromFileL3V2_4 < Test::Unit::TestCase

  def test_echo_l3v2_empty_math
    expected = "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n" + 
    "<sbml xmlns=\"http://www.sbml.org/sbml/level3/version2/core\" " + 
    "level=\"3\" version=\"2\">\n"  + 
    "  <model substanceUnits=\"mole\" timeUnits=\"second\" volumeUnits=\"litre\" " + 
    "areaUnits=\"metre\" lengthUnits=\"metre\" extentUnits=\"mole\" " + 
    "conversionFactor=\"p\">\n" + 
    "    <listOfCompartments>\n" + 
    "      <compartment id=\"c\" constant=\"true\"/>\n" + 
    "    </listOfCompartments>\n" + 
    "    <listOfSpecies>\n" + 
    "      <species id=\"s\" compartment=\"c\" hasOnlySubstanceUnits=\"false\" boundaryCondition=\"false\" constant=\"false\"/>\n" + 
    "    </listOfSpecies>\n" + 
    "    <listOfParameters>\n" + 
    "      <parameter id=\"p\" value=\"1\" units=\"dimensionless\" constant=\"true\"/>\n" + 
    "    </listOfParameters>\n" + 
    "    <listOfInitialAssignments>\n" + 
    "      <initialAssignment symbol=\"p\"/>\n" + 
    "    </listOfInitialAssignments>\n" + 
    "    <listOfRules>\n" + 
    "      <algebraicRule/>\n" + 
    "    </listOfRules>\n" + 
    "    <listOfReactions>\n" + 
    "      <reaction id=\"r\" reversible=\"false\">\n" + 
    "        <listOfReactants>\n" + 
    "          <speciesReference species=\"s\" constant=\"true\"/>\n" + 
    "        </listOfReactants>\n" + 
    "        <kineticLaw/>\n" + 
    "      </reaction>\n" + 
    "    </listOfReactions>\n" + 
    "  </model>\n" + 
    "</sbml>\n"
    filename = "../../sbml/sbml/test/test-data/"
    filename += "l3v2-empty-math.xml"
    fileout = "../../sbml/sbml/test/test-data/"
    fileout += "tempOut.xml"
    d = LibSBML::readSBML()
    if (d == nil)
    end
    LibSBML::writeSBML(d)
    outD = LibSBML::readSBML()
    m = outd.getModel()
    assert( m != nil )
    assert( m.getNumUnitDefinitions() == 0 )
    assert( m.getNumFunctionDefinitions() == 0 )
    assert( m.getNumCompartments() == 1 )
    assert( m.getNumSpecies() == 1 )
    assert( m.getNumParameters() == 1 )
    assert( m.getNumRules() == 1 )
    assert( m.getNumInitialAssignments() == 1 )
    assert( m.getNumConstraints() == 0 )
    assert( m.getNumReactions() == 1 )
    assert( m.getNumEvents() == 0 )
    ia = m.getInitialAssignment(0)
    assert( ia != nil )
    assert( ia.isSetMath() == false )
    r = m.getReaction(0)
    assert( r != nil )
    kl = r.getKineticLaw()
    assert( kl != nil )
    assert( kl.isSetMath() == false )
    S = LibSBML::writeSBMLToString(outD)
    assert_equal true, equals(expected,S)
    d = nil
    outD = nil
  end

  def test_read_l3v2_empty_math
    reader = LibSBML::SBMLReader.new()
    filename = "../../sbml/sbml/test/test-data/"
    filename += "l3v2-empty-math.xml"
    d = reader.readSBML(filename)
    if (d == nil)
    end
    assert( d.getLevel() == 3 )
    assert( d.getVersion() == 2 )
    m = d.getModel()
    assert( m != nil )
    assert( m.getNumUnitDefinitions() == 0 )
    assert( m.getNumFunctionDefinitions() == 0 )
    assert( m.getNumCompartments() == 1 )
    assert( m.getNumSpecies() == 1 )
    assert( m.getNumParameters() == 1 )
    assert( m.getNumRules() == 1 )
    assert( m.getNumInitialAssignments() == 1 )
    assert( m.getNumConstraints() == 0 )
    assert( m.getNumReactions() == 1 )
    assert( m.getNumEvents() == 0 )
    ia = m.getInitialAssignment(0)
    assert( ia != nil )
    assert( ia.isSetMath() == false )
    r = m.getReaction(0)
    assert( r != nil )
    assert( r.getNumReactants() == 1 )
    kl = r.getKineticLaw()
    assert( kl != nil )
    assert( kl.isSetMath() == false )
    d = nil
  end

end
