  def test_XMLAttributes_add1
    xa = LibSBML::XMLAttributes.new()
    xt2 = LibSBML::XMLTriple.new("name2", "http://name2.org/", "p2")
    i = xa.add( "name1", "val1", "http://name1.org/", "p1")
    assert( i == LibSBML::LIBSBML_OPERATION_SUCCESS )
    i = xa.add(xt2, "val2")
    assert( i == LibSBML::LIBSBML_OPERATION_SUCCESS )
    assert( xa.getLength() == 2 )
    assert( xa.isEmpty() == false )
    i = xa.add( "noprefix", "val3")
    assert( i == LibSBML::LIBSBML_OPERATION_SUCCESS )
    assert( xa.getLength() == 3 )
    assert( xa.isEmpty() == false )
    xa = nil
    xt2 = nil
  end

  def test_XMLAttributes_add_remove_qname_C
    xa = LibSBML::XMLAttributes.new()
    xt1 = LibSBML::XMLTriple.new("name1", "http://name1.org/", "p1")
    xt2 = LibSBML::XMLTriple.new("name2", "http://name2.org/", "p2")
    xt3 = LibSBML::XMLTriple.new("name3", "http://name3.org/", "p3")
    xt1a = LibSBML::XMLTriple.new("name1", "http://name1a.org/", "p1a")
    xt2a = LibSBML::XMLTriple.new("name2", "http://name2a.org/", "p2a")
    xa.add( "name1", "val1", "http://name1.org/", "p1")
    xa.add(xt2, "val2")
    assert( xa.getLength() == 2 )
    assert( xa.isEmpty() == false )
    test = xa.getName(0)
    assert( (  "name1" != test ) == false )
    test = nil
    test = xa.getValue(0)
    assert( (  "val1"  != test ) == false )
    test = nil
    test = xa.getURI(0)
    assert( (  "http://name1.org/" != test ) == false )
    test = nil
    test = xa.getPrefix(0)
    assert( (  "p1"    != test ) == false )
    test = nil
    test = xa.getName(1)
    assert( (  "name2" != test ) == false )
    test = nil
    test = xa.getValue(1)
    assert( (  "val2"  != test ) == false )
    test = nil
    test = xa.getURI(1)
    assert( (  "http://name2.org/" != test ) == false )
    test = nil
    test = xa.getPrefix(1)
    assert( (  "p2"    != test ) == false )
    test = nil
    test = xa.getValue( "name1")
    assert( (  "val1"  != test ) == false )
    test = nil
    test = xa.getValue( "name2")
    assert( (  "val2"  != test ) == false )
    test = nil
    test = xa.getValue( "name1", "http://name1.org/")
    assert( (  "val1"  != test ) == false )
    test = nil
    test = xa.getValue( "name2", "http://name2.org/")
    assert( (  "val2"  != test ) == false )
    test = nil
    test = xa.getValue(xt1)
    assert( (  "val1"  != test ) == false )
    test = nil
    test = xa.getValue(xt2)
    assert( (  "val2"  != test ) == false )
    test = nil
    assert( xa.hasAttribute(-1) == false )
    assert( xa.hasAttribute(2) == false )
    assert( xa.hasAttribute(0) == true )
    assert( xa.hasAttribute( "name1", "http://name1.org/") == true )
    assert( xa.hasAttribute( "name2", "http://name2.org/") == true )
    assert( xa.hasAttribute( "name3", "http://name3.org/") == false )
    assert( xa.hasAttribute(xt1) == true )
    assert( xa.hasAttribute(xt2) == true )
    assert( xa.hasAttribute(xt3) == false )
    xa.add( "noprefix", "val3")
    assert( xa.getLength() == 3 )
    assert( xa.isEmpty() == false )
    test = xa.getName(2)
    assert( (  "noprefix" != test ) == false )
    test = nil
    test = xa.getValue(2)
    assert( (  "val3"  != test ) == false )
    test = nil
    assert( xa.getURI(2) == "" )
    assert( xa.getPrefix(2) == "" )
    test = xa.getValue( "noprefix", "")
    assert( (  "val3" != test ) == false )
    test = nil
    assert( xa.hasAttribute( "noprefix"    ) == true )
    assert( xa.hasAttribute( "noprefix", "") == true )
    xa.add(xt1, "mval1")
    xa.add( "name2", "mval2", "http://name2.org/", "p2")
    xa.add( "noprefix", "mval3")
    assert( xa.getLength() == 3 )
    assert( xa.isEmpty() == false )
    test = xa.getName(0)
    assert( (  "name1" != test ) == false )
    test = nil
    test = xa.getValue(0)
    assert( (  "mval1"  != test ) == false )
    test = nil
    test = xa.getURI(0)
    assert( (  "http://name1.org/" != test ) == false )
    test = nil
    test = xa.getPrefix(0)
    assert( (  "p1"    != test ) == false )
    test = nil
    test = xa.getName(1)
    assert( (  "name2" != test ) == false )
    test = nil
    test = xa.getValue(1)
    assert( (  "mval2"  != test ) == false )
    test = nil
    test = xa.getURI(1)
    assert( (  "http://name2.org/" != test ) == false )
    test = nil
    test = xa.getPrefix(1)
    assert( (  "p2"    != test ) == false )
    test = nil
    test = xa.getName(2)
    assert( (  "noprefix" != test ) == false )
    test = nil
    test = xa.getValue(2)
    assert( (  "mval3"  != test ) == false )
    test = nil
    assert( xa.getURI(2) == "" )
    assert( xa.getPrefix(2) == "" )
    assert( xa.hasAttribute(xt1) == true )
    assert( xa.hasAttribute( "name1", "http://name1.org/") == true )
    assert( xa.hasAttribute( "noprefix") == true )
    xa.add(xt1a, "val1a")
