    assert( (  "p1a"    != test ) == false )
    test = nil
    test = xa.getName(4)
    assert( (  "name2" != test ) == false )
    test = nil
    test = xa.getValue(4)
    assert( (  "val2a"  != test ) == false )
    test = nil
    test = xa.getURI(4)
    assert( (  "http://name2a.org/" != test ) == false )
    test = nil
    test = xa.getPrefix(4)
    assert( (  "p2a"    != test ) == false )
    test = nil
    test = xa.getValue( "name1")
    assert( (  "mval1"  != test ) == false )
    test = nil
    test = xa.getValue( "name2")
    assert( (  "mval2"  != test ) == false )
    test = nil
    test = xa.getValue( "name1", "http://name1a.org/")
    assert( (  "val1a"  != test ) == false )
    test = nil
    test = xa.getValue( "name2", "http://name2a.org/")
    assert( (  "val2a"  != test ) == false )
    test = nil
    test = xa.getValue(xt1a)
    assert( (  "val1a"  != test ) == false )
    test = nil
    test = xa.getValue(xt2a)
    assert( (  "val2a"  != test ) == false )
    test = nil
    xa.remove(xt1a)
    xa.remove(xt2a)
    assert( xa.getLength() == 3 )
    xa.remove( "name1", "http://name1.org/")
    assert( xa.getLength() == 2 )
    assert( xa.isEmpty() == false )
    test = xa.getName(0)
    assert( (  "name2" != test ) == false )
    test = nil
    test = xa.getValue(0)
    assert( (  "mval2"  != test ) == false )
    test = nil
    test = xa.getURI(0)
    assert( (  "http://name2.org/" != test ) == false )
    test = nil
    test = xa.getPrefix(0)
    assert( (  "p2"    != test ) == false )
    test = nil
    test = xa.getName(1)
    assert( (  "noprefix" != test ) == false )
    test = nil
    test = xa.getValue(1)
    assert( (  "mval3"  != test ) == false )
    test = nil
    assert( xa.getURI(1) == "" )
    assert( xa.getPrefix(1) == "" )
    assert( xa.hasAttribute( "name1", "http://name1.org/") == false )
    xa.remove(xt2)
    assert( xa.getLength() == 1 )
    assert( xa.isEmpty() == false )
    test = xa.getName(0)
    assert( (  "noprefix" != test ) == false )
    test = nil
    test = xa.getValue(0)
    assert( (  "mval3"  != test ) == false )
    test = nil
    assert( xa.getURI(0) == "" )
    assert( xa.getPrefix(0) == "" )
    assert( xa.hasAttribute(xt2) == false )
    assert( xa.hasAttribute( "name2", "http://name2.org/") == false )
    xa.remove( "noprefix", "")
    assert( xa.getLength() == 0 )
    assert( xa.isEmpty() == true )
    assert( xa.hasAttribute( "noprefix"    ) == false )
    assert( xa.hasAttribute( "noprefix", "") == false )
    xa = nil
    xt1 = nil
    xt2 = nil
    xt3 = nil
    xt1a = nil
    xt2a = nil
  end

  def test_XMLAttributes_clear1
    xa = LibSBML::XMLAttributes.new()
    xt2 = LibSBML::XMLTriple.new("name2", "http://name2.org/", "p2")
    i = xa.add( "name1", "val1", "http://name1.org/", "p1")
    i = xa.add(xt2, "val2")
    i = xa.add( "noprefix", "val3")
    assert( xa.getLength() == 3 )
    assert( xa.isEmpty() == false )
    i = xa.clear()
    assert( i == LibSBML::LIBSBML_OPERATION_SUCCESS )
    assert( xa.getLength() == 0 )
    assert( xa.isEmpty() == true )
    xa = nil
    xt2 = nil
  end

  def test_XMLAttributes_remove1
    xa = LibSBML::XMLAttributes.new()
    xt2 = LibSBML::XMLTriple.new("name2", "http://name2.org/", "p2")
    i = xa.add( "name1", "val1", "http://name1.org/", "p1")
    assert( i == LibSBML::LIBSBML_OPERATION_SUCCESS )
    i = xa.add(xt2, "val2")
    assert( i == LibSBML::LIBSBML_OPERATION_SUCCESS )
    i = xa.add( "noprefix", "val3")
    assert( i == LibSBML::LIBSBML_OPERATION_SUCCESS )
    i = xa.add( "name4", "val4", "http://name4.org/", "p1")
    assert( i == LibSBML::LIBSBML_OPERATION_SUCCESS )
    assert( xa.getLength() == 4 )
    i = xa.remove(4)
    assert( i == LibSBML::LIBSBML_INDEX_EXCEEDS_SIZE )
    i = xa.remove(3)
    assert( i == LibSBML::LIBSBML_OPERATION_SUCCESS )
    assert( xa.getLength() == 3 )
    i = xa.remove( "noprefix")
    assert( i == LibSBML::LIBSBML_OPERATION_SUCCESS )
    assert( xa.getLength() == 2 )
    i = xa.remove(xt2)
    assert( i == LibSBML::LIBSBML_OPERATION_SUCCESS )
    assert( xa.getLength() == 1 )
    i = xa.remove( "name1", "http://name1.org/")
    assert( i == LibSBML::LIBSBML_OPERATION_SUCCESS )
    assert( xa.getLength() == 0 )
    xa = nil
    xt2 = nil
  end

end
