require 'test/unit'
require 'libSBML'

class TestXMLNamespaces < Test::Unit::TestCase

  def setup
    @@ns = LibSBML::XMLNamespaces.new()
    if (@@ns == nil)
    end
  end

  def teardown
    @@ns = nil
  end

  def test_XMLNamespaces_accessWithNULL
    assert( nil.add(nil,nil) == LibSBML::LIBSBML_INVALID_OBJECT )
    assert( nil.clear() == LibSBML::LIBSBML_OPERATION_FAILED )
    assert( nil.clone() == nil )
    assert( nil.getIndex(nil) == -1 )
    assert( nil.getIndexByPrefix(nil) == -1 )
    assert( nil.getLength() == 0 )
    assert( nil.getPrefix(0) == "" )
    assert( nil.getPrefix(nil) == "" )
    assert( nil.getURI(0) == "" )
    assert( nil.getURI(nil) == "" )
    assert( nil.hasNS(nil,nil) == false )
    assert( nil.hasPrefix(nil) == false )
    assert( nil.hasURI(nil) == false )
    assert( nil.isEmpty() == true )
    assert( nil.remove(0) == LibSBML::LIBSBML_INVALID_OBJECT )
    assert( nil.remove(nil) == LibSBML::LIBSBML_INVALID_OBJECT )
  end

  def test_XMLNamespaces_add
    assert( @@ns.getLength() == 0 )
    assert( @@ns.isEmpty() == true )
    @@ns.add( "http://test1.org/", "test1")
    assert( @@ns.getLength() == 1 )
    assert( @@ns.isEmpty() == false )
    @@ns.add( "http://test2.org/", "test2")
    assert( @@ns.getLength() == 2 )
    assert( @@ns.isEmpty() == false )
    @@ns.add( "http://test1.org/", "test1a")
    assert( @@ns.getLength() == 3 )
    assert( @@ns.isEmpty() == false )
    @@ns.add( "http://test1.org/", "test1a")
    assert( @@ns.getLength() == 3 )
    assert( @@ns.isEmpty() == false )
    assert( !( @@ns.getIndex( "http://test1.org/") == -1) )
  end

  def test_XMLNamespaces_add1
    assert( @@ns.getLength() == 0 )
    assert( @@ns.isEmpty() == true )
    i = @@ns.add( "http://test1.org/", "test1")
    assert( i == LibSBML::LIBSBML_OPERATION_SUCCESS )
    assert( @@ns.getLength() == 1 )
    assert( @@ns.isEmpty() == false )
    test = @@ns.getPrefix(0)
    assert( (  "test1" != test ) == false )
    test = nil
    test = @@ns.getPrefix( "http://test1.org/")
    assert( (  "test1" != test ) == false )
    test = nil
    test = @@ns.getURI(0)
    assert( (  "http://test1.org/" != test ) == false )
    test = nil
    test = @@ns.getURI( "test1")
    assert( (  "http://test1.org/" != test ) == false )
    test = nil
    i = @@ns.add( "http://test2.org/", "test1")
    assert( i == LibSBML::LIBSBML_OPERATION_SUCCESS )
    assert( @@ns.getLength() == 1 )
    assert( @@ns.isEmpty() == false )
    test = @@ns.getPrefix(0)
    assert( (  "test1" != test ) == false )
    test = nil
    test = @@ns.getPrefix( "http://test2.org/")
    assert( (  "test1" != test ) == false )
    test = nil
    test = @@ns.getURI(0)
    assert( (  "http://test2.org/" != test ) == false )
    test = nil
    test = @@ns.getURI( "test1")
    assert( (  "http://test2.org/" != test ) == false )
    test = nil
    i = @@ns.add( "http://test.org/", "")
    assert( i == LibSBML::LIBSBML_OPERATION_SUCCESS )
    assert( @@ns.getLength() == 2 )
    assert( @@ns.isEmpty() == false )
    assert( @@ns.getPrefix(1) == "" )
    assert( @@ns.getPrefix( "http://test.org/") == "" )
    test = @@ns.getURI(1)
    assert( (  "http://test.org/" != test ) == false )
    test = nil
    i = @@ns.add( "http://test3.org/", "")
    assert( i == LibSBML::LIBSBML_OPERATION_SUCCESS )
    assert( @@ns.getLength() == 2 )
    assert( @@ns.isEmpty() == false )
    assert( @@ns.getPrefix(1) == "" )
    assert( @@ns.getPrefix( "http://test3.org/") == "" )
    test = @@ns.getURI(1)
    assert( (  "http://test3.org/" != test ) == false )
    test = nil
    i = @@ns.add( "http://www.sbml.org/sbml/level1", "")
    assert( i == LibSBML::LIBSBML_OPERATION_SUCCESS )
    assert( @@ns.getLength() == 2 )
    assert( @@ns.isEmpty() == false )
    assert( @@ns.getPrefix(1) == "" )
    assert( @@ns.getPrefix(                      "http://www.sbml.org/sbml/level1") == "" )
    test = @@ns.getURI(1)
    assert( (  "http://www.sbml.org/sbml/level1" != test ) == false )
    test = nil
    i = @@ns.add( "http://test_sbml_prefix/", "")
    assert( i == LibSBML::LIBSBML_OPERATION_FAILED )
    assert( @@ns.getLength() == 2 )
    assert( @@ns.isEmpty() == false )
    assert( @@ns.getPrefix(1) == "" )
    assert( @@ns.getPrefix(                      "http://www.sbml.org/sbml/level1") == "" )
    test = @@ns.getURI(1)
    assert( (  "http://www.sbml.org/sbml/level1" != test ) == false )
    test = nil
    i = @@ns.add( "http://www.sbml.org/sbml/level1", "newprefix")
    assert( i == LibSBML::LIBSBML_OPERATION_SUCCESS )
    assert( @@ns.getLength() == 3 )
    assert( @@ns.isEmpty() == false )
    test = @@ns.getPrefix(2)
    assert( (  "newprefix" != test ) == false )
    test = nil
    assert( @@ns.getPrefix(                      "http://www.sbml.org/sbml/level1") == "" )
    test = @@ns.getURI(2)
    assert( (  "http://www.sbml.org/sbml/level1" != test ) == false )
    test = nil
    i = @@ns.add( "http://www.foo", "newprefix")
    assert( i == LibSBML::LIBSBML_OPERATION_FAILED )
    assert( @@ns.getLength() == 3 )
    assert( @@ns.isEmpty() == false )
    test = @@ns.getPrefix(2)
    assert( (  "newprefix" != test ) == false )
    test = nil
    test = @@ns.getURI(2)
    assert( (  "http://www.sbml.org/sbml/level1" != test ) == false )
    test = nil
    i = @@ns.add( "http://www.sbml.org/sbml/level2", "newprefix")
    assert( i == LibSBML::LIBSBML_OPERATION_FAILED )
    assert( @@ns.getLength() == 3 )
    assert( @@ns.isEmpty() == false )
    test = @@ns.getPrefix(2)
    assert( (  "newprefix" != test ) == false )
    test = nil
    test = @@ns.getURI(2)
    assert( (  "http://www.sbml.org/sbml/level1" != test ) == false )
    test = nil
  end

